jQuery(document).ready(function(){$(".qtyplus").click(function(e){e.preventDefault(),fieldName=$(this).attr("name");var a=parseInt($("input[name="+fieldName+"]").val());isNaN(a)?$("input[name="+fieldName+"]").val(1):$("input[name="+fieldName+"]").val(a+1)}),$(".qtyminus").click(function(e){e.preventDefault(),fieldName=$(this).attr("name");var a=parseInt($("input[name="+fieldName+"]").val());!isNaN(a)&&a>0?$("input[name="+fieldName+"]").val(a-1):$("input[name="+fieldName+"]").val(0)})});